#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#include "util.h"

int test_strtolower( char *in, char *out )
{
    char *test;
    printf( "   strtolower( '%s', '%s') ", in, out );

    test = strtolower( strdup( in ) );

    printf( " = '%s' ", test );

    if ( strlen( test ) == strlen( out ) ) {
        if ( strncmp( test, out, strlen(out) ) == 0 ) {
            printf( "passed\n" );
            free( test );
            return( 0 );
        }
    }
    printf( "failed\n" );
    free( test );
    return( 1 );
}

